import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StudentForm = () => {
    const [studname, setStudName] = useState('');
    const [email, setEmail] = useState('');
    const [editingId, setEditingId] = useState(null);

    useEffect(() => {
        // Fetch data if editingId is provided
        if (editingId) {
            axios.get(`http://127.0.0.1:8000/api/students/${editingId}/`)
                .then(response => {
                    const { studname, email } = response.data;
                    setStudName(studname);
                    setEmail(email);
                })
                .catch(error => console.error('Error fetching student data:', error));
        }
    }, [editingId]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            let response;
            if (editingId) {
                response = await axios.put(`http://127.0.0.1:8000/api/students/${editingId}/`, { studname, email });
            } else {
                response = await axios.post('http://127.0.0.1:8000/api/create-student/', { studname, email });
            }
            console.log('Data submitted:', response.data);
            setStudName('');
            setEmail('');
            setEditingId(null);
        } catch (error) {
            console.error('Error submitting data:', error);
        }
    };

    const handleEdit = (id) => {
        setEditingId(id);
    };

    const handleDelete = async (id) => {
        try {
            const response = await axios.delete(`http://127.0.0.1:8000/api/students/${id}/`);
            console.log('Data deleted:', response.data);
            // Optionally, you can handle UI changes after deletion
        } catch (error) {
            console.error('Error deleting data:', error);
        }
    };

    return (
        <div>
            <br />
            <br />
            <h2>{editingId ? 'Edit Student' : 'Add Student'}</h2>
            <br />
            <form onSubmit={handleSubmit}>
                <label>
                    Student Name:
                    <input type="text" value={studname} onChange={(e) => setStudName(e.target.value)} />
                </label>
                <br />
                <br />
                <label>
                    Email:
                    <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} />
                </label>
                <br />
                <br />
                <button type="submit">{editingId ? 'Update' : 'Submit'}</button>
            </form>
            <br />
            {editingId && <button onClick={() => setEditingId(null)}>Cancel Edit</button>}
            <br />
            <h2>Students</h2>
            {/* Render list of students with edit and delete buttons */}
        </div>
    );
};

export default StudentForm;
